// Swiper.jsのインポート
import React from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Autoplay, Navigation, Pagination } from 'swiper';
import 'swiper/css';
import 'swiper/css/navigation';
import 'swiper/css/pagination';
import './SliderComponent.css'

function SliderComponent() {
  return (
    <div className="slider-container">
      <Swiper
        spaceBetween={30}        // スライド間のスペース
        slidesPerView={3}        // 画面に表示するスライド数
        navigation               // 前後のナビゲーション矢印を有効化
        pagination={{ clickable: true }}  // ページネーション（下の丸いボタン）を有効化
        autoplay={{              // 自動スライド設定
          delay: 2500,           // 2.5秒ごとにスライド
          disableOnInteraction: false,
        }}
        loop={true}              // スライドをループさせる
      >
        {/* スライドの内容 */}
        <SwiperSlide>
          <div className="slide-content">
            <img src={`${process.env.PUBLIC_URL}/slider1.png`} alt= "Slider 1" />
            <h3>SHOW</h3>
            <p>企画 3選</p>
          </div>
        </SwiperSlide>

        <SwiperSlide>
          <div className="slide-content">
            <img src={`${process.env.PUBLIC_URL}/slider2.png`} alt="Slider 2" />
            <h3>音楽</h3>
            <p>企画 3選</p>
          </div>
        </SwiperSlide>

        <SwiperSlide>
          <div className="slide-content">
            <img src={`${process.env.PUBLIC_URL}/slider3.png`} alt="Slider 3" />
            <h3>その他</h3>
            <p>企画 3選</p>
          </div>
        </SwiperSlide>

        {/* 追加のスライドはここに */}
      </Swiper>
    </div>
  );
}

export default SliderComponent;
